/* modulo 42 - pagina de Checkout - criando o componente de dados  de pagamento 2/2*/

import React, { Component } from 'react';


class DadosPedido extends Component {

	render() {
		const { opcaoPagamentoSelecionada } = this.state;
		return (
			<div className='Dados-Pedido-Container'>
				<h2>DADOS DO PEDIDO</h2>
				<br />
				{this.renderOpcoesPagamento()}
				<br />
				<br/>
				{opcaoPagamentoSelecionada === 'boleto' && this.renderPagamentoComBoleto()}
				{opcaoPagamentoSelecionada === 'cartao' && this.renderPagamentoComCartao()}
			</div>
		);
	}
}

export default DadosPedido;
