import axios from 'axios';

 export const consultaCep = (cep) => {
 


	const endereco = {

		"local": "",
		"bairro": "",
		"cidade": "",
		"estado": ""
	}


   axios.get(`https://viacep.com.br/ws/${cep.replace('-', '')}/json`)
	   .then((response) => {		
		
		  
						
						endereco.local = response.data["logradouro"],
						endereco.bairro = response.data["bairro"],
					    endereco.cidade = response.data["localidade"],
			           endereco.estado = response.data["uf"]
		                
		                console.log('meu', endereco);  
		                 return endereco;
		   

		   
		   
		   
		   
						
					})
	   .catch(e => { return endereco })
	 
	
	
	
}
			

export default consultaCep;
;
