/* modulo 43 - Meus Pedidos -  
criando o componente  de Menu e adicionando o estilo */


import React, { Component } from 'react';
import { formatMoney } from '../../utils';
import Link from 'next/link';

/* Modulos 51 - Pedidos - realizando a integração*/

import moment from 'moment';

class PedidoCard extends Component{

	render() {
		
		const { pedido } = this.props;
		const { _id, createdAt, pagamento, entrega, cancelado } = pedido;

		return (
			<div className='Pedido-Card flex'>
				<div className='flex-1 flex-start'>
					<span>{moment(createdAt).format('DD/MM/YYYY HH:mm')}</span>
				</div>

				<div className='flex-1 flex-start'>
					<span>{formatMoney(pagamento.valor)}</span>
				</div>

				<div className='flex-3 flex-start'>
					<span>{cancelado ? "Cancelado" : `${pagamento.status} / ${entrega.status}`}</span>
				</div>

				<div className='flex-1 flex-start'>
					<Link href={`/area-cliente/pedido/${_id}`}>
						<span className='btn btn-primary btn-sm'>DETALHES</span>
					</Link>
				</div>
			</div>
		);
	}
}

export default PedidoCard;
