/* modulo 43 - Area do cliente -  Detalhes do pedido - 
 criando a página, componentes e todo o estilo  1/3
*/


/*Módulo 51 -  detalhes do Pedido - realizando a integração 1/2 */

import { connect } from 'react-redux';
import actions from '../../redux/actions';

import React, { Component } from 'react';
import MenuAreaDoCliente from '../Menu/AreaDoCliente';
import PedidoDetalhes from './PedidoDetalhes'; 

class PedidoContainer extends Component{


	fetchPedido() {
		
		const { token, fetchPedido } = this.props;
		const { pedido } = this.props.query;
		if (token && pedido) fetchPedido(pedido, token);
	}

	componentDidMount() {
		
		this.fetchPedido();
	}

	componentDidUpdate() {
		
		if (!this.props.pedido) this.fetchPedido();
	}

	componentWillUnmount() {
		 
		this.props.cleanPedido();
	 }

	render() {
		
		return (
			<div className="Pedido-Container container-big flex horizontal">
				<MenuAreaDoCliente />
				<PedidoDetalhes/>
			</div>
		)
	}

}

const mapStateToProps = state => ({

	token: state.auth.token,
	pedido: state.pedido.pedido


})

export default connect(mapStateToProps,actions)(PedidoContainer);
