/* modulo 41 - loja virtual - desenvolvendo o head padr찾o */



import React from 'react';
import NextHead from 'next/head';
import { string } from 'prop-types';

/* Open Ghuelf */
const defaultDescricao = '';
const defaultOGURL = '';
const defaultOGImage = "/static/logo.png";

/*M처dulo 49 atualizando a Page e o Head */

const Head = (props) => (
	<NextHead>
		<meta charSet='UTF-8' />
		<title>{props.title || ''}</title>
		<meta name='description' content={props.description || defaultDescricao} />
		<meta name='viewport' content='with=device=width, initial-scale=1' />
		<link rel='icon' href='/static/favicon.ico' />
		<meta property='og:url' content={props.url || defaultOGURL} />
		<meta property='og:title' content={props.title || ''} />
		<meta property='og:description' content={props.description || defaultDescricao} />
		<meta property='twitter:site' content={props.url || defaultOGURL} />
		<meta property='twitter:card' content='summary_large_image' />
		<meta property='twitter:image' content={props.ogImage || defaultOGImage} />
		<meta property='og:image' content={props.ogImage || defaultOGImage} />
		<meta property='og:image:width' content='600' />
		<meta property='og:image:height' content='600' />
		<link rel='stylesheet' href='/static/font-awesome.min.css' />
		<link
			rel='stylesheet'
			href='https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css'
			integrity='sha384-wvfXpqpZZVQGK6TAh5PVlGOfQNHSoD2xbE+QkPxCAFlNEevoEH3Sl0sibVcOQVnN'
			crossOrigin='anonymous'
		/>
		<link rel="stylesheet" href="/static/style.css" />
		{props.children}
	</NextHead>
);

Head.propTypes = {

	title: string,
	description: string,
	url: string,
	ogImage : string
}

export default Head;
