/* modulo 42 - paginas do produto -  criando componente de descrição */
import React from 'react';


const DESC = "Ótimo produto, de muita tecnologia.\n\nAltar disponibilidade e segurança.\n\nGarantia em todo o Brasil por 1 ano."

const Descricao = () => (
	
	<div className="Descricao flex vertical">
		<h2>Descrição</h2>
		<br />
		<div>
			{DESC.split("\n").map((item, idx) => <p key={idx}>{item}</p> )}
		</div>

	</div>
);

export default Descricao
