/* modulo 41 - loja virtual -  adicionando paginação configurações e estilo */

import React, { Component} from 'react';
import Produtos from '../../../components/Listas/Produtos';
import Paginacao from '../../../components/Paginacao';

const PRODUTOS = [
	{
		id: 1,
